SELECT m.meal_no AS mealNo, meal_reg_date AS mealRegDate, meal_time AS mealTime, meal_title AS mealTitle, meal_content AS mealContent,
f.food_code AS foodCode, food_amount AS foodAmount,
food_name AS foodName, food_sub_group AS foodSubGroup, (food_weight * food_amount) as foodWeight, (nutri_energy * food_amount) as nutriEnergy, (nutri_carbohydrate * food_amount) as nutriCarbohydrate, (nutri_protein * food_amount) as nutriProtein, (nutri_total_fatty_acids * food_amount) as nutriTotalFattyAcids
FROM meal m JOIN meal_food mf ON m.meal_no = mf.meal_no JOIN food f ON mf.food_code = f.food_code WHERE m.meal_no = #{mealNo}
