SELECT m.meal_no as mealNo, meal_title as mealTitle, (food_amount * food_weight) as foodAmount FROM meal m JOIN meal_food mf ON m.meal_no = mf.meal_no JOIN food f ON mf.food_code = f.food_code WHERE meal_time = 'WATER' AND user_no = #{userNo} AND meal_reg_date = #{mealRegDate}
SELECT meal_reg_date, SUM(f.nutri_energy) AS energy, SUM(f.nutri_carbohydrate) AS carbohydrate, SUM(f.nutri_sugar) AS sugar, SUM(f.nutri_dietary_fiber) AS dietaryFiber, SUM(f.nutri_protein) AS protein, SUM(f.nutri_ash) AS ash, SUM(f.nutri_total_fatty_acids) AS totalFattyAcids, SUM(f.nutri_saturated_fats) AS saturatedFats, SUM(f.nutri_monounsaturated_fats + f.nutri_polyunsaturated_fats) AS unsaturatedFats, SUM(f.nutri_water) AS water, SUM(f.nutri_vitamin_a_carotene) AS vitaminACarotene, SUM(f.nutri_vitamin_b1) AS vitaminB1, SUM(f.nutri_vitamin_b2) AS vitaminB2, SUM(f.nutri_niacin) AS niacin, SUM(f.nutri_folate) AS folate, SUM(f.nutri_vitamin_c) AS vitaminC, SUM(f.nutri_vitamin_d) AS vitaminD, SUM(f.nutri_cholesterol) AS cholesterol, SUM(f.nutri_sodium) AS sodium, SUM(f.nutri_calcium) AS calcium, SUM(f.nutri_iron) AS iron, SUM(f.nutri_phosphorus) AS phosphorus, SUM(f.nutri_potassium) AS potassium FROM meal m JOIN meal_food mf ON m.meal_no = mf.meal_no JOIN food f ON mf.food_code = f.food_code WHERE user_no = #{userNo} AND meal_reg_date = #{mealRegDate} GROUP BY meal_reg_date
SELECT meal_reg_date, SUM(f.nutri_energy) AS energy, SUM(f.nutri_carbohydrate) AS carbohydrate, SUM(f.nutri_sugar) AS sugar, SUM(f.nutri_dietary_fiber) AS dietaryFiber, SUM(f.nutri_protein) AS protein, SUM(f.nutri_ash) AS ash, SUM(f.nutri_total_fatty_acids) AS totalFattyAcids, SUM(f.nutri_saturated_fats) AS saturatedFats, SUM(f.nutri_monounsaturated_fats + f.nutri_polyunsaturated_fats) AS unsaturatedFats, SUM(f.nutri_water) AS water, SUM(f.nutri_vitamin_a_carotene) AS vitaminACarotene, SUM(f.nutri_vitamin_b1) AS vitaminB1, SUM(f.nutri_vitamin_b2) AS vitaminB2, SUM(f.nutri_niacin) AS niacin, SUM(f.nutri_folate) AS folate, SUM(f.nutri_vitamin_c) AS vitaminC, SUM(f.nutri_vitamin_d) AS vitaminD, SUM(f.nutri_cholesterol) AS cholesterol, SUM(f.nutri_sodium) AS sodium, SUM(f.nutri_calcium) AS calcium, SUM(f.nutri_iron) AS iron, SUM(f.nutri_phosphorus) AS phosphorus, SUM(f.nutri_potassium) AS potassium FROM meal m JOIN meal_food mf ON m.meal_no = mf.meal_no JOIN food f ON mf.food_code = f.food_code WHERE m.user_no = 1 AND m.meal_reg_date BETWEEN DATE_SUB(CURDATE(), INTERVAL 6 DAY) AND CURDATE() GROUP BY meal_reg_date ORDER BY meal_reg_date
SELECT weekday, AVG(energy) AS energy, AVG(carbohydrate) AS carbohydrate, AVG(sugar) AS sugar, AVG(dietaryFiber) AS dietaryFiber, AVG(protein) AS protein, AVG(ash) AS ash, AVG(totalFattyAcids) AS totalFattyAcids, AVG(saturatedFats) AS saturatedFats, AVG(unsaturatedFats) AS unsaturatedFats, AVG(water) AS water, AVG(vitaminACarotene) AS vitaminACarotene, AVG(vitaminB1) AS vitaminB1, AVG(vitaminB2) AS vitaminB2, AVG(niacin) AS niacin, AVG(folate) AS folate, AVG(vitaminC) AS vitaminC, AVG(vitaminD) AS vitaminD, AVG(cholesterol) AS cholesterol, AVG(sodium) AS sodium, AVG(calcium) AS calcium, AVG(iron) AS iron, AVG(phosphorus) AS phosphorus, AVG(potassium) AS potassium FROM ( SELECT DATE(m.meal_reg_date) AS date, DAYNAME(m.meal_reg_date) AS weekday, SUM(f.nutri_energy) AS energy, SUM(f.nutri_carbohydrate) AS carbohydrate, SUM(f.nutri_sugar) AS sugar, SUM(f.nutri_dietary_fiber) AS dietaryFiber, SUM(f.nutri_protein) AS protein, SUM(f.nutri_ash) AS ash, SUM(f.nutri_total_fatty_acids) AS totalFattyAcids, SUM(f.nutri_saturated_fats) AS saturatedFats, SUM(f.nutri_monounsaturated_fats + f.nutri_polyunsaturated_fats) AS unsaturatedFats, SUM(f.nutri_water) AS water, SUM(f.nutri_vitamin_a_carotene) AS vitaminACarotene, SUM(f.nutri_vitamin_b1) AS vitaminB1, SUM(f.nutri_vitamin_b2) AS vitaminB2, SUM(f.nutri_niacin) AS niacin, SUM(f.nutri_folate) AS folate, SUM(f.nutri_vitamin_c) AS vitaminC, SUM(f.nutri_vitamin_d) AS vitaminD, SUM(f.nutri_cholesterol) AS cholesterol, SUM(f.nutri_sodium) AS sodium, SUM(f.nutri_calcium) AS calcium, SUM(f.nutri_iron) AS iron, SUM(f.nutri_phosphorus) AS phosphorus, SUM(f.nutri_potassium) AS potassium FROM meal m JOIN meal_food mf ON m.meal_no = mf.meal_no JOIN food f ON mf.food_code = f.food_code WHERE m.user_no = 1 AND m.meal_reg_date BETWEEN DATE_SUB(CURDATE(), INTERVAL 29 DAY) AND CURDATE() GROUP BY DATE(m.meal_reg_date) ) AS daily_totals GROUP BY weekday ORDER BY FIELD(weekday, 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday')
