mapper.update:
UPDATE comment
SET comment_content = #{commentContent}
WHERE comment_no = #{commentNo} AND user_no = #{userNo}

mapper.select:
SELECT * FROM comment
WHERE user_no = #{userNo}
ORDER BY comment_reg_date DESC

SELECT * FROM comment
WHERE board_no = #{boardNo}
ORDER BY comment_parent_no ASC, comment_reg_date ASC
